{
  "@graph" : [ {
    "@id" : "http://open-services.net/ns/asset#",
    "@type" : "owl:Ontology",
    "description" : "\n\t\t\tAll vocabulary URIs defined in the OSLC\n\t\t\tAsset Management (Asset) namespace.\n\t\t",
    "title" : "The OSLC Asset Management(Asset) Vocabulary\n\t\t",
    "label" : "Asset Management(Asset)",
    "seeAlso" : [ "http://open-services.net/ns/asset/asset.rdf", "http://open-services.net/bin/view/Main/AssetMgSpecificationV2", "http://open-services.net/bin/view/Main/AssetHome" ]
  }, {
    "@id" : "http://open-services.net/ns/asset#Artifact",
    "@type" : "rdfs:Class",
