    "label" : "content",
    "range" : "http://open-services.net/ns/asset#Artifact",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Artifact"
  }, {
    "@id" : "http://open-services.net/ns/asset#guid",
    "@type" : "rdf:Property",
    "comment" : "An identifier for the asset. Assigned by the service provider\n\t\t    when a resource is created. Different versions of the same asset will \n\t\t    share the same identifier.",
    "isDefinedBy" : "http://open-services.net/ns/asset#",
    "label" : "guid",
    "range" : "http://open-services.net/ns/asset#Asset",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Asset"
  }, {
    "@id" : "http://open-services.net/ns/asset#identifier",
    "@type" : "rdf:Property",
    "comment" : "A unique identifier for a resource. Assigned by the service provider when \n\t\t\ta resource is created. ",
    "isDefinedBy" : "http://open-services.net/ns/asset#",
    "label" : "identifier",
    "range" : "http://open-services.net/ns/asset#Asset",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Asset"
  }, {
    "@id" : "http://open-services.net/ns/asset#manufacturer",
    "@type" : "rdf:Property",
    "comment" : "The name of the asset manufacturer.",
    "isDefinedBy" : "http://open-services.net/ns/asset#",
    "label" : "manufacturer",
    "range" : "http://open-services.net/ns/asset#Asset",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Asset"
  }, {
    "@id" : "http://open-services.net/ns/asset#model",
    "@type" : "rdf:Property",
    "comment" : "The value of the asset model.",
