    "label" : "model",
    "range" : "http://open-services.net/ns/asset#Asset",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Asset"
  }, {
    "@id" : "http://open-services.net/ns/asset#relationshipType",
    "@type" : "rdf:Property",
    "comment" : "The type of this relationship from the perspective of the \n\t\t    oslc_asset:relatedAsset resource based on values defined by the service provider.\n\t\t    This specification does not define the resource for this property, however it \n\t\t    should contain a dcterms:title property.\n\t\t",
    "isDefinedBy" : "http://open-services.net/ns/asset#",
    "label" : "relationshipType",
    "range" : "http://open-services.net/ns/asset#Asset",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Asset"
  }, {
    "@id" : "http://open-services.net/ns/asset#serialNumber",
    "@type" : "rdf:Property",
    "comment" : "The serial number assigned by the asset manufacturer.",
    "isDefinedBy" : "http://open-services.net/ns/asset#",
    "label" : "serialNumber",
    "range" : "http://open-services.net/ns/asset#Asset",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Asset"
  }, {
    "@id" : "http://open-services.net/ns/asset#size",
    "@type" : "rdf:Property",
    "comment" : "The size of the artifact media resource in bytes.\n\t\t",
    "isDefinedBy" : "http://open-services.net/ns/asset#",
    "label" : "size",
    "range" : "http://open-services.net/ns/asset#Artifact",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Artifact"
  }, {
    "@id" : "http://open-services.net/ns/asset#state",
    "@type" : "rdf:Property",
    "comment" : "Used to indicate the state of the asset based on values defined by the service provider. \n\t\tThis specification does not define the resource for this property, however it should contain a \n\t\tdcterms:title property.",
    "isDefinedBy" : "http://open-services.net/ns/asset#",
    "label" : "state",
    "range" : "http://open-services.net/ns/asset#Asset",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Asset"
  }, {
    "@id" : "http://open-services.net/ns/asset#tag",
    "@type" : "rdf:Property",
    "comment" : "Specifies the asset tag value for an Asset. Asset tags are typically human \n\t\t    readable labels. For hardware assets, these tags are durable, securely attached to \n\t\t    equipment, and may also be readable by barcode and/or RFID.",
    "isDefinedBy" : "http://open-services.net/ns/asset#",
    "label" : "tag",
    "range" : "http://open-services.net/ns/asset#Asset",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Asset"
  }, {
    "@id" : "http://open-services.net/ns/asset#version",
    "@type" : "rdf:Property",
    "comment" : "The version of the asset. Possible values may include '1.0', \n\t\t    '2.0', etc.",
    "isDefinedBy" : "http://open-services.net/ns/asset#",
    "label" : "version",
    "range" : "http://open-services.net/ns/asset#Asset",
    "seeAlso" : "http://open-services.net/bin/view/Main/AssetMgSpecificationV2#Asset"
  }, {
    "@id" : "http://open-services.net/ns/asset/asset.rdf",
    "nature" : "http://www.w3.org/2000/01/rdf-schema#"
  } ],
  "@context" : {
    "seeAlso" : {
      "@id" : "http://www.w3.org/2000/01/rdf-schema#seeAlso",
      "@type" : "@id"
    },
    "range" : {
      "@id" : "http://www.w3.org/2000/01/rdf-schema#range",
      "@type" : "@id"
    },
    "comment" : {
      "@id" : "http://www.w3.org/2000/01/rdf-schema#comment"
    },
    "label" : {
      "@id" : "http://www.w3.org/2000/01/rdf-schema#label"
    },
    "isDefinedBy" : {
      "@id" : "http://www.w3.org/2000/01/rdf-schema#isDefinedBy",
      "@type" : "@id"
    },
    "nature" : {
      "@id" : "http://www.rddl.org/nature",
      "@type" : "@id"
    },
    "description" : {
      "@id" : "http://purl.org/dc/terms/description",
      "@type" : "http://www.w3.org/1999/02/22-rdf-syntax-ns#XMLLiteral"
    },
    "title" : {
      "@id" : "http://purl.org/dc/terms/title"
    },
    "rdf" : "http://www.w3.org/1999/02/22-rdf-syntax-ns#",
    "owl" : "http://www.w3.org/2002/07/owl#",
    "dcterms" : "http://purl.org/dc/terms/",
    "rdfs" : "http://www.w3.org/2000/01/rdf-schema#",
    "rddl" : "http://www.rddl.org/"
  }
}

