        rdfs:comment      "Shorter form of dcterms:identifier for the resource." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "discussedBy" ;
        rdfs:comment      "A series of notes and comments about this resource." ;
