        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "statusCode" ;
        rdfs:comment      "The HTTP status code reported with the error." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "message" ;
        rdfs:comment      "An informative message describing the error that occurred." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "extendedError" ;
        rdfs:comment      "Extended (additional) error information." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "moreInfo" ;
        rdfs:comment      "A resource giving more information on the error SHOULD be of an HTML content-type." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "rel" ;
        rdfs:comment      "If present and set to 'alternate' then indicates that work-around is provided, behavior for other values is undefined." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "results" ;
        rdfs:comment      "Used to hold the results of dialog action or JSON query results (default).\n\t\tThe JSON query result attribute 'oslc:results' is used whenever a provider doesn't have\n\t\ta suitable property already in its model for such purposes." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "totalCount" ;
        rdfs:comment      "This optional property indicates the total number of results across all pages, \n\t\tits value should be non-negative. In the context of a query resource, this value SHOULD \n\t\tbe the total number of results, i.e. the number of resources that match the query. \n\t\tIn the context of other resources, the value SHOULD be the total number of property \n\t\tvalues (i.e. RDF triples) of the resource. Unless Stable Paging is in effect, the \n\t\ttotal count MAY vary as a client retrieves subsequent pages." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "nextPage" ;
        rdfs:comment      "Link to next page of response." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "describes" ;
        rdfs:comment      "This shape describes resources that are of the RDF type given by the object of the oslc:describes predicate. Formally, a shape S applies to a resource R if there is a triple R rdf:type T and there is a triple S oslc:describes T." ;
        rdfs:seeAlso       ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "property" ;
        rdfs:comment      "The properties that are allowed or required by this shape." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "allowedValue" ;
        rdfs:comment      "value allowed for a property." ;
        rdfs:seeAlso       .


        rdf:type          rdf:Property ;
        rdfs:isDefinedBy   ;
        rdfs:label        "allowedValues" ;
        rdfs:comment      "Resource with allowed values for the property being defined. Range of oslc:AllowedValues" ;
        rdfs:range         ;
